mapper.select:
SELECT t.task_id, t.user_id, t.name, t.importance, t.deadline, t.status, t.completed_date, t.created_at, t.updated_at, u.name AS user_name FROM tasks t LEFT JOIN users u ON t.user_id = u.user_id
t.task_id = #{form.taskId}
AND t.user_id = #{form.userId}
AND t.name LIKE CONCAT('%', #{form.taskName}, '%')
AND t.importance = #{form.importance}
AND t.deadline < #{form.deadline}
AND t.status = #{form.status}
AND t.completed_date < #{form.completedDate}
AND u.name LIKE CONCAT('%', #{form.userName}, '%')
ORDER BY
task_id user_id task_id
ASC DESC ,t.task_id DESC

mapper.update:

UPDATE
		tasks
SET
		status = #{expired}
WHERE
		user_id = #{userId}
AND
		status = #{incomplete}
AND
		deadline < CURRENT_DATE

mapper.delete:


DELETE FROM
		tasks
WHERE
		task_id = #{taskId}
AND
		user_id = #{userId}
